import styled from "styled-components";
import { Button, Container } from "react-bootstrap";
import "./App.css";
import { useEffect, useState } from "react";
import ErrorBody from "./ErrorBody";
import {
  fetchEmployees,
  fetchFinished,
  fetchPartners,
  fetchTasks
} from "./Service";

const Note = styled.div`
  text-decoration: none;
  margin: 20px 20px 20px 20px;
  align-items: center;
  color: #000;
  background: white;
  height: 220px;
  width: 300px;
  padding: 1em;
`;

const TaskName = styled.div`
  text-align: center;
  font-size: 30px;
`;

const Partner = styled.div`
  text-align: center;
  font-size: 20px;
  margin-top: 10px;
`;

const Statistics = () => {
  const [task, setTasks] = useState([]);
  const [partners, setPartners] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [finished, setFinished] = useState([]);
  const [error, setError] = useState(null);

  const getTasks = async () => {
    const result = await fetchTasks();
    setTasks(result);
  };

  const getPartners = async () => {
    const result = await fetchPartners();
    setPartners(result);
  };

  const getEmployees = async () => {
    const result = await fetchEmployees();
    setEmployees(result);
  };

  const getFinished = async () => {
    const result = await fetchFinished();
    setFinished(result);
  };

  useEffect(() => {
    getTasks().catch((err) => setError(err.massage));
    getPartners().catch((err) => setError(err.massage));
    getEmployees().catch((err) => setError(err.massage));
    getFinished().catch((err) => setError(err.massage));
  }, []);

  return (
    <div className="note">
      {error ? (
        <ErrorBody error={error} />
      ) : (
        employees.map((employee) => {
          return (
            <div key={employee.id}>
              <Note>
                <TaskName>{employee.name}</TaskName>
                <Partner>{employee.email}</Partner>
                <Button variant="outline-secondary" className="about">
                  Részletek megjelenítése
                </Button>
                <Button variant="outline-secondary" className="edit">
                  Szerkesztés
                </Button>
              </Note>
            </div>
          );
        })
      )}
    </div>
  );
};

export default Statistics;
