import { useNavigate, useParams } from "react-router-dom";
import { useEffect, useState } from "react";

const EditTask = () => {
  const id = useParams().id;
  const navigate = useNavigate();

  const [success, setSuccess] = useState(false);
  const [hasError, setHasError] = useState(false);

  const [updatedTask, setUpdatedTask] = useState({});

  const title = id ? "Feladat módosítása" : "Új feladat hozzáadása";
  const submitButtonLabel = id ? "Módosítás" : "Hozzáadás";

  const fetchTaskById = async (id) => {
    const response = await fetchTaskById(id);
    setUpdatedTask(response);
  };

  useEffect(() => {
    if (success) {
      navigate("/feladatok");
    }
  }, [success]);

  const submitForm = (e) => {
    if (id) {
      updateTask(updatedTask, id, setSuccess, setHasError);
    } else {
      addVendor(updatedTask, setSuccess, setHasError);
    }
    e.preventDefault();
  };

  return (
    <Form className="container mb-3" onSubmit={submitForm}>
      <h1 className="my-3">{title}</h1>
      <FormTextInput
        label="Árus neve"
        dataObject={updatedVendor}
        dataObjectKey="name"
        setter={setUpdatedVendor}
        required={true}
      />
      <FormTextInput
        label="Árus logója"
        dataObject={updatedVendor}
        dataObjectKey="profilePic"
        setter={setUpdatedVendor}
        required={true}
        type="url"
      />
      <FormTextAreaInput
        label="Árus rövid bemutatkozása"
        dataObject={updatedVendor}
        dataObjectKey="intro"
        setter={setUpdatedVendor}
        rows={2}
        maxLength={100}
      />
      <FormTextAreaInput
        label="Árus hosszú leírása"
        dataObject={updatedVendor}
        dataObjectKey="introductionLong"
        setter={setUpdatedVendor}
        rows={5}
        maxLength={2500}
      />
      <VendorCardPaymentCheckbox
        dataObject={updatedVendor}
        setter={setUpdatedVendor}
      />
      <VendorProductsInput
        dataObject={updatedVendor}
        setter={setUpdatedVendor}
      />
      <FormTextInput
        label="Árus telefonszáma"
        dataObject={updatedVendor}
        dataObjectKey="phone"
        setter={setUpdatedVendor}
        type="tel"
      />
      <FormTextInput
        label="Árus e-mail címe"
        dataObject={updatedVendor}
        dataObjectKey="email"
        setter={setUpdatedVendor}
        type="email"
      />
      <FormTextInput
        label="Árus facebook linkje"
        dataObject={updatedVendor}
        dataObjectKey="facebook"
        setter={setUpdatedVendor}
      />
      <FormTextInput
        label="Árus instagram linkje"
        dataObject={updatedVendor}
        dataObjectKey="instagram"
        setter={setUpdatedVendor}
      />
      <FormTextInput
        label="Árus honlap címe"
        dataObject={updatedVendor}
        dataObjectKey="webSite"
        setter={setUpdatedVendor}
        type="url"
      />
      <VendorDetailsButtons submitButtonLabel={submitButtonLabel} />
      {hasError && (
        <p className="text-danger mt-3">Hiba történt a beküldés során!</p>
      )}
    </Form>
  );
};
export default EditTask;
