import styled from "styled-components";
import "./App.css";
import { useEffect, useState } from "react";
import {
  fetchEmployees,
  fetchFinished,
  fetchPartners,
  fetchTasks
} from "./Service";
import { Table } from "react-bootstrap";

const Note = styled.div`
  text-decoration: none;
  margin: 20px 20px 20px 20px;
  text-align: center;
  padding-top: 33px;
  color: #000;
  background: white;
  height: 100px;
  width: 300px;
  border-style: groove;
`;

const Statistics = () => {
  const [task, setTasks] = useState([]);
  const [partners, setPartners] = useState([]);
  const [employees, setEmployees] = useState([]);
  const [finished, setFinished] = useState([]);
  const [error, setError] = useState(null);

  const getTasks = async () => {
    const result = await fetchTasks();
    setTasks(result);
  };

  const getPartners = async () => {
    const result = await fetchPartners();
    setPartners(result);
  };

  const getEmployees = async () => {
    const result = await fetchEmployees();
    setEmployees(result);
  };

  const getFinished = async () => {
    const result = await fetchFinished();
    setFinished(result);
  };

  useEffect(() => {
    getTasks().catch((err) => setError(err.massage));
    getPartners().catch((err) => setError(err.massage));
    getEmployees().catch((err) => setError(err.massage));
    getFinished().catch((err) => setError(err.massage));
  }, []);

  return (
    <div>
      <div className="note">
        <Note>Feladatok száma: {task.length}</Note>
        <Note>Partnerek száma: {partners.length}</Note>
        <Note>Dolgozók száma: {employees.length}</Note>
        <Note>Elvégzett feladatok száma: {finished.length}</Note>
      </div>
      Feladatok
      <Table striped bordered hover variant="dark">
        <thead>
          <tr>
            <th>#</th>
            <th>First Name</th>
            <th>Last Name</th>
            <th>Username</th>
          </tr>
        </thead>
        <tbody>
          <tr>
            <td>1</td>
            <td>Mark</td>
            <td>Otto</td>
            <td>@mdo</td>
          </tr>
          <tr>
            <td>2</td>
            <td>Jacob</td>
            <td>Thornton</td>
            <td>@fat</td>
          </tr>
          <tr>
            <td>3</td>
            <td colSpan={2}>Larry the Bird</td>
            <td>@twitter</td>
          </tr>
        </tbody>
      </Table>
    </div>
  );
};

export default Statistics;
