import styled from "styled-components";
import { Button, Container } from "react-bootstrap";
import "./App.css";
import { useEffect, useState } from "react";
import { fetchTasksById } from "./Service";
import { useParams } from "react-router-dom";

const Note = styled.div`
  text-decoration: none;
  margin: 20px 20px 20px 20px;
  align-items: center;
  color: #000;
  background: white;
  height: 220px;
  width: 300px;
  padding: 1em;
`;

const DetailsName = styled.div`
  padding-top: 60px;
  text-align: center;
  font-size: 30px;
`;

const DetailsDescription = styled.div`
  padding-top: 80px;
  text-align: center;
  font-size: 30px;
`;

const About = () => {
  const { id } = useParams();

  const [task, setTask] = useState(null);
  const [error, setError] = useState(null);

  const getTasksById = async (id) => {
    const result = await fetchTasksById(id);
    setTask(result);
  };

  useEffect(() => {
    if (id) {
      getTasksById(id);
    }
  }, []);

  return (
    <div className="note">
      {task ? (
        <>
          <DetailsName>{task.name}</DetailsName>
          <DetailsDescription>{task.description}</DetailsDescription>
        </>
      ) : (
        <div>Betöltés folyamatban...</div>
      )}
    </div>
  );
};

export default About;
