import { fetchMarketById, updateMarket } from "components/Service";
import { useMutation, useQuery } from "react-query";
import { Navigate, useNavigate, useParams } from "react-router-dom";
import { MarketInput } from "./Market";
import MarketForm from "./MarketForm";

const EditTask = () => {
  const navigate = useNavigate();

  const id = useParams().id ?? "";

  const taskQuery = useQuery("task", () => fetchTaskById(id), {
    cacheTime: 0
  });

  const editTaskMutation = useMutation((TaskInput) => updateTask(task, id), {
    onSuccess: () => navigate("/feladatok")
  });

  const submitForm = (TaskInput) => editTaskMutation.mutate(task);

  if (!id) {
    return <Navigate to="/feladatok" replace />;
  }

  const { isLoading, isIdle, isError } = taskQuery;

  if (isLoading || isIdle) {
    return <>Betöltés...</>;
  }

  if (isError) {
    return <>Hiba történt a betöltés során!</>;
  }

  const task = taskQuery.data;

  const defaultValues: MarketInput = {
    ...market,
    vendors: market.vendors.map((vendor) => vendor.id)
  };

  return (
    <MarketForm
      title="Piac módosítása"
      submitLabel="Módosítás"
      hasError={editMarketMutation.isError}
      errorMessage="Nem sikerült a piacot módosítani!"
      onSubmit={submitForm}
      defaultValues={defaultValues}
    />
  );
};

export default EditTask;
