import styled from "styled-components";
import "./App.css";
import { Button } from "react-bootstrap";
import { useState, useEffect } from "react";
import { useNavigate } from "react-router-dom";
import { addFinished, addTask, fetchEmployees } from "./Service";
import { Form } from "react-bootstrap";
import FormTextInput from "./FormTextInput";
import FormTextAreaInput from "./FormTextAreaInput";

const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Wrapper = styled.div`
  margin-top: 30px;
  background-color: #fff;
  width: 550px;
  height: 750px;
  padding: 30px;
  box-sizing: border-box;
  border-radius: 0px;
  border-style: groove;
`;

const Name = styled.div`
  margin: 10px;
  color: #969292;
  font-size: 16px;
  text-align: center;
`;

const Desc = styled.div`
  margin: 20px;
  color: #969292;
  font-size: 16px;
  text-align: center;
`;

const AddNew = styled.div`
  display: flex;
  padding-left: 80px;
`;

const NewFinished = () => {
  const navigate = useNavigate();

  const [newTask, setNewTask] = useState("");
  const [success, setSuccess] = useState(false);
  const [Error, setError] = useState(false);

  const [employees, setEmployees] = useState();

  const getEmployees = async () => {
    const result = await fetchEmployees();
    setEmployees(result);
  };

  const submitForm = (e) => {
    addFinished(newTask, setSuccess, setError);
  };

  useEffect(() => {
    getEmployees().catch((err) => setError(err.massage));
  }, []);

  return (
    <Container>
      <Wrapper>
        <Form onSubmit={submitForm}>
          <Name>
            <FormTextInput
              label="Feladat neve"
              dataObject={newTask}
              dataObjectKey="name"
              setter={setNewTask}
              required={true}
            />
          </Name>
          <Desc>
            <FormTextInput
              label="Munkaidő órában"
              dataObject={newTask}
              dataObjectKey="time"
              setter={setNewTask}
              required={true}
            />
          </Desc>
          <Desc>
            <FormTextInput
              label="Munkatárs"
              dataObject={newTask}
              dataObjectKey="employee"
              setter={setNewTask}
              required={true}
            />

            <Form.Select path="employees">
              <option items="${employees}" />
            </Form.Select>
          </Desc>
          <Desc>
            <FormTextInput
              label="Feladat"
              dataObject={newTask}
              dataObjectKey="task"
              setter={setNewTask}
              required={true}
            />
          </Desc>
          <AddNew>
            <Button variant="outline-secondary" className="edit" type="submit">
              Feladat hozzáadása
            </Button>
          </AddNew>
        </Form>
      </Wrapper>
    </Container>
  );
};

export default NewFinished;
