import { useNavigate } from "react-router-dom";
import { fetchTasksById } from "./Service";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { Form } from "react-bootstrap";
import styled from "styled-components";

const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Wrapper = styled.div`
  margin-top: 30px;
  background-color: #fff;
  width: 550px;
  height: 750px;
  padding: 30px;
  box-sizing: border-box;
  border-radius: 0px;
  border-style: groove;
`;

const EditTask = () => {
  const id = useParams().id;
  const navigate = useNavigate();

  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  const [updatedTask, setUpdatedTask] = useState({});

  const getTask = async (id) => {
    const response = await fetchTasksById(id);
    setUpdatedTask(response);
  };

  const submitForm = (e) => {
    updatedTask(updatedTask, id, setSuccess, setError);
  };

  useEffect(() => {
    getTask().catch((err) => setError(err.massage));
  }, []);
  return (
    <Container>
      <Wrapper>
        <Form onSubmit={submitForm}>
          <Name>
            <FormTextInput
              label="Feladat neve"
              dataObject={newTask}
              dataObjectKey="name"
              setter={setNewTask}
              required={true}
            />
          </Name>
          <Name>
            <FormTextAreaInput
              label="Feladat leírása"
              dataObject={newTask}
              dataObjectKey="intro"
              setter={setNewTask}
              rows={15}
              maxLength={100}
              required={true}
              name="fullDescription"
              dataObjectKey="description"
            />
          </Name>

          <Desc>
            <FormTextInput
              label="Partner"
              dataObject={newTask}
              dataObjectKey="part"
              setter={setNewTask}
              required={true}
            />
          </Desc>
          <AddNew>
            <Button variant="outline-secondary" className="edit" type="submit">
              Feladat hozzáadása
            </Button>
          </AddNew>
        </Form>
      </Wrapper>
    </Container>
  );
};
export default EditTask;
