import { useNavigate, useParams } from "react-router-dom";
import { useEffect, useState } from "react";

const EditTask = () => {
  const id = useParams().id;
  const navigate = useNavigate();

  const [success, setSuccess] = useState(false);
  const [hasError, setHasError] = useState(false);

  const [updatedTask, setUpdatedTask] = useState({});

  const title = id ? "Feladat módosítása" : "Új feladat hozzáadása";
  const submitButtonLabel = id ? "Módosítás" : "Hozzáadás";

  const fetchTaskById = async (id) => {
    const response = await fetchTaskById(id);
    setUpdatedTask(response);
  };

  useEffect(() => {
    if (success) {
      navigate("/feladatok");
    }
  }, [success]);

  const submitForm = (e) => {
    if (id) {
      updateTask(updatedTask, id, setSuccess, setHasError);
    } else {
      addVendor(updatedTask, setSuccess, setHasError);
    }
    e.preventDefault();
  };

  return (
    <Form className="container mb-3" onSubmit={submitForm}>
      <h1 className="my-3">{name}</h1>
      <FormTextInput
        label="Árus neve"
        dataObject={updatedTask}
        dataObjectKey="name"
        setter={setUpdatedVendor}
        required={true}
      />

      <VendorDetailsButtons submitButtonLabel={submitButtonLabel} />
      {hasError && (
        <p className="text-danger mt-3">Hiba történt a beküldés során!</p>
      )}
    </Form>
  );
};
export default EditTask;
