import { useNavigate } from "react-router-dom";
import { fetchTasksById, updateTask } from "./Service";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { Form } from "react-bootstrap";
import styled from "styled-components";
import FormTextInput from "./FormTextInput";
import FormTextAreaInput from "./FormTextAreaInput";
import { Button } from "react-bootstrap";

const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Wrapper = styled.div`
  margin-top: 30px;
  background-color: #fff;
  width: 550px;
  height: 750px;
  padding: 30px;
  box-sizing: border-box;
  border-radius: 0px;
  border-style: groove;
`;

const Name = styled.div`
  margin: 10px;
  color: #969292;
  font-size: 16px;
  text-align: center;
`;

const Desc = styled.div`
  margin: 20px;
  color: #969292;
  font-size: 16px;
  text-align: center;
`;

const AddNew = styled.div`
  display: flex;
  padding-left: 100px;
`;

const Delete = styled.div`
  display: flex;
  padding-left: 117px;
`;

const EditTask = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  const [updatedTask, setUpdatedTask] = useState({});

  const getTask = async (id) => {
    const response = await fetchTasksById(id);
    setUpdatedTask(response);
    console.log(response);
  };

  const submitForm = (e) => {
    updateTask(updatedTask, id, setSuccess, setError);
    e.preventDefault();
  };

  useEffect(() => {
    if (id) {
      getTask(id);
    }
  }, []);
  return (
    <Container>
      <Wrapper>
        <Form onSubmit={submitForm}>
          <Name>
            <FormTextInput
              label="Feladat neve"
              dataObject={updatedTask}
              dataObjectKey="name"
              setter={setUpdatedTask}
              required={true}
            />
          </Name>
          <Name>
            <FormTextAreaInput
              label="Feladat leírása"
              dataObject={updatedTask}
              dataObjectKey="intro"
              setter={setUpdatedTask}
              rows={15}
              maxLength={100}
              required={true}
              name="fullDescription"
              dataObjectKey="description"
            />
          </Name>

          <Desc>
            <FormTextInput
              label="Partner"
              dataObject={updatedTask}
              dataObjectKey="part"
              setter={setUpdatedTask}
              required={true}
            />
          </Desc>
          <AddNew>
            <Button variant="outline-secondary" className="edit" type="submit">
              Szerkesztés
            </Button>
          </AddNew>
          <Delete>
            <Button variant="outline-secondary" className="edit">
              Törlés
            </Button>
          </Delete>
        </Form>
      </Wrapper>
    </Container>
  );
};
export default EditTask;
