import { useNavigate } from "react-router-dom";
import {
  deleteFinishedById,
  fetchFinishedById,
  fetchFinishedsById,
  updateFinished
} from "./Service";
import { useParams } from "react-router-dom";
import { useState, useEffect } from "react";
import { Form } from "react-bootstrap";
import styled from "styled-components";
import FormTextInput from "./FormTextInput";
import FormTextAreaInput from "./FormTextAreaInput";
import { Button } from "react-bootstrap";
import DeleteEntity from "./DeleteEntity";

const Container = styled.div`
  width: 100%;
  display: flex;
  justify-content: center;
  align-items: center;
`;

const Wrapper = styled.div`
  margin-top: 30px;
  background-color: #fff;
  width: 550px;
  height: 750px;
  padding: 30px;
  box-sizing: border-box;
  border-radius: 0px;
  border-style: groove;
`;

const Name = styled.div`
  margin: 10px;
  color: #969292;
  font-size: 16px;
  text-align: center;
`;

const Desc = styled.div`
  margin: 20px;
  color: #969292;
  font-size: 16px;
  text-align: center;
`;

const AddNew = styled.div`
  display: flex;
  padding-left: 100px;
`;

const Delete = styled.div`
  display: flex;
  padding-left: 123px;
`;

const EditFinished = () => {
  const { id } = useParams();
  const navigate = useNavigate();

  const [success, setSuccess] = useState(false);
  const [error, setError] = useState(null);

  const [updatedFinished, setUpdatedFinished] = useState({});

  const getFinished = async (id) => {
    const response = await fetchFinishedById;
    setUpdatedFinished(response);
  };

  const deleteClick = (e) => {
    deleteFinishedById(id, setSuccess, setError);
  };

  const submitForm = (e) => {
    updateFinished(updatedFinished, id, setSuccess, setError);
    e.preventDefault();
  };

  useEffect(() => {
    if (id) {
      getFinished(id);
    }
  }, []);

  useEffect(() => {
    if (success) {
      navigate("/teljesitett");
    }
  }, [success]);

  return (
    <Container>
      <Wrapper>
        <div key={updatedFinished.id}>
          <Form onSubmit={submitForm}>
            <Name>
              <FormTextInput
                label="Feladat neve"
                dataObject={updatedFinished}
                dataObjectKey="name"
                setter={setUpdatedFinished}
                required={true}
              />
            </Name>
            <Name>
              <FormTextAreaInput
                label="Feladat ideje"
                dataObject={updatedFinished}
                dataObjectKey="time"
                setter={setUpdatedFinished}
                rows={15}
                maxLength={100}
                required={true}
                name="fullDescription"
                dataObjectKey="description"
              />
            </Name>

            <Desc>
              <FormTextInput
                label="Munkatársak"
                dataObject={updatedFinished}
                dataObjectKey="employee"
                setter={setUpdatedFinished}
                required={true}
              />
            </Desc>

            <Desc>
              <FormTextInput
                label="Feladat"
                dataObject={updatedFinished}
                dataObjectKey="task"
                setter={setUpdatedFinished}
                required={true}
              />
            </Desc>
            <AddNew>
              <Button
                variant="outline-secondary"
                className="editButton"
                type="submit"
              >
                Szerkesztés
              </Button>
              <div className="deleteButton">
                <DeleteEntity
                  confirmationQuestion={`Biztosan kitörlöd a következő feladatot? ${updatedFinished.name}`}
                  handleDelete={deleteClick}
                  ID={updatedFinished.id}
                />
              </div>
            </AddNew>
          </Form>
        </div>
      </Wrapper>
    </Container>
  );
};
export default EditFinished;
