import styled from "styled-components";
import "./App.css";
import { Button, Container } from "react-bootstrap";
import { BrowserRouter, Link } from "react-router-dom";
import Ticket from "./Tickets";
import { fetchTasks } from "./Service";
import { useState, useEffect } from "react";
import axios from "axios";

const cors = require("cors");

const Title = styled.div`
  padding-top: 20px;
  padding-bottom: 20px;
  text-align: center;
  font-size: 40px;
  color: white;
  font-family: "Noto Serif", serif;
`;

const Completed = styled.div`
  display: grid;
  text-align: center;
  text-decoration: none;
  color: white;
  padding-bottom: 30px;
`;

const Home = () => {
  const axios = require("axios");
  const [task, setTask] = useState([]);
  const [error, setError] = useState(null);

  const getTasks = async () => {
    const url = "http://localhost:8080/api/tasks";
    try {
      const response = await axios.get(url);
      const data = response.data;
      return data;
    } catch (error) {
      console.warn("Failed to load tasks");
      throw Error("Nem sikerült betölteni a feladatok listáját!");
    }
  };

  const Completed = async () => {
    const url = "http://localhost:8080/api/tasks";
    const completed = true;

    if (completed) {
      try {
        const response = await axios.get(url);
        const data = response.data;
        return data;
      } catch (error) {
        console.warn("Failed to load tasks");
        throw Error("Nem sikerült betölteni a feladatok listáját!");
      }
    }
  };

  async function getDataById() {
    const id = get_id.current.value;

    if (id) {
      try {
        const res = await apiClient.get(`/tutorials/${id}`);

        const result = {
          data: res.data,
          status: res.status,
          statusText: res.statusText,
          headers: res.headers
        };

        setGetResult(fortmatResponse(result));
      } catch (err) {
        setGetResult(fortmatResponse(err.response?.data || err));
      }
    }
  }

  useEffect(() => {
    getTasks();
  }, []);

  return (
    <BrowserRouter>
      <div className="Home">
        <Title>Feladatok</Title>

        <Completed>
          <nav>
            <Link to="/uncompleted">
              <Button className="Uncompleted" variant="dark">
                Elvégzendő feladatok
              </Button>
            </Link>
          </nav>

          <Link to="/completed">
            <Button className="Completed" variant="dark">
              Teljesített feladatok
            </Button>
          </Link>

          <Link to="all">
            <Button className="All" variant="dark">
              Összes feladat
            </Button>
          </Link>

          <Link to="new">
            <Button className="Add" variant="dark">
              Feladat hozzáadása
            </Button>
          </Link>
        </Completed>
        <Ticket />
      </div>
    </BrowserRouter>
  );
};

export default Home;
